/// Enum to represent either a single value or a nested list (one level deep).
#[derive(Debug, Clone, PartialEq)]
pub enum NestedItem<T> {
    Single(T),
    List(Vec<T>),
}

/// Flatten a nested list one level deep.
///
/// # Arguments
/// * `nested` - A vector of NestedItem elements
///
/// # Returns
/// A flattened vector containing all elements
///
/// # Examples
/// ```
/// use rust_utils::list_utils::{flatten, NestedItem};
/// let nested = vec![
///     NestedItem::List(vec![1, 2]),
///     NestedItem::List(vec![3, 4]),
/// ];
/// assert_eq!(flatten(nested), vec![1, 2, 3, 4]);
/// ```
pub fn flatten<T: Clone>(nested: Vec<NestedItem<T>>) -> Vec<T> {
    let mut result = Vec::new();
    for item in nested {
        match item {
            NestedItem::Single(val) => result.push(val),
            NestedItem::List(list) => result.extend(list),
        }
    }
    result
}

#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn test_flatten() {
        // Test [[1, 2], [3, 4]] -> [1, 2, 3, 4]
        let nested = vec![NestedItem::List(vec![1, 2]), NestedItem::List(vec![3, 4])];
        assert_eq!(flatten(nested), vec![1, 2, 3, 4]);

        // Test [1, [2, 3], 4] -> [1, 2, 3, 4]
        let mixed = vec![
            NestedItem::Single(1),
            NestedItem::List(vec![2, 3]),
            NestedItem::Single(4),
        ];
        assert_eq!(flatten(mixed), vec![1, 2, 3, 4]);

        // Test [] -> []
        let empty: Vec<NestedItem<i32>> = vec![];
        assert_eq!(flatten(empty), Vec::<i32>::new());

        // Test [[1], [], [2]] -> [1, 2]
        let with_empty = vec![
            NestedItem::List(vec![1]),
            NestedItem::List(vec![]),
            NestedItem::List(vec![2]),
        ];
        assert_eq!(flatten(with_empty), vec![1, 2]);
    }
}

