use std::collections::HashMap;
use std::hash::Hash;

/// Invert a dictionary (swap keys and values).
///
/// # Arguments
/// * `d` - A HashMap to invert
///
/// # Returns
/// A new HashMap with keys and values swapped
///
/// # Examples
/// ```
/// use std::collections::HashMap;
/// use rust_utils::dict_utils::invert_dict;
///
/// let mut map = HashMap::new();
/// map.insert("a", 1);
/// map.insert("b", 2);
///
/// let inverted = invert_dict(map);
/// assert_eq!(inverted.get(&1), Some(&"a"));
/// assert_eq!(inverted.get(&2), Some(&"b"));
/// ```
pub fn invert_dict<K, V>(d: HashMap<K, V>) -> HashMap<V, K>
where
    K: Clone,
    V: Clone + Eq + Hash,
{
    d.into_iter().map(|(k, v)| (v, k)).collect()
}

#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn test_invert_dict() {
        // Test {"a": 1, "b": 2} -> {1: "a", 2: "b"}
        let mut map: HashMap<&str, i32> = HashMap::new();
        map.insert("a", 1);
        map.insert("b", 2);
        let inverted = invert_dict(map);
        assert_eq!(inverted.get(&1), Some(&"a"));
        assert_eq!(inverted.get(&2), Some(&"b"));

        // Test empty dict
        let empty: HashMap<&str, i32> = HashMap::new();
        let inverted_empty = invert_dict(empty);
        assert!(inverted_empty.is_empty());

        // Test {1: "x"} -> {"x": 1}
        let mut int_key: HashMap<i32, &str> = HashMap::new();
        int_key.insert(1, "x");
        let inverted_int = invert_dict(int_key);
        assert_eq!(inverted_int.get(&"x"), Some(&1));
    }
}

