/// Check if a string looks like a valid email (simple check).
///
/// This performs a simple email validation:
/// - Must not be empty
/// - Must contain exactly one "@" symbol
/// - Local part (before @) must not be empty
/// - Domain part (after @) must not be empty
/// - Domain must contain at least one "."
///
/// # Arguments
/// * `email` - The email string to validate
///
/// # Returns
/// `true` if the email passes basic validation, `false` otherwise
///
/// # Examples
/// ```
/// use rust_utils::validation::is_valid_email;
/// assert!(is_valid_email("user@example.com"));
/// assert!(!is_valid_email("invalid"));
/// ```
pub fn is_valid_email(email: &str) -> bool {
    if email.is_empty() || !email.contains('@') {
        return false;
    }

    let parts: Vec<&str> = email.split('@').collect();
    if parts.len() != 2 {
        return false;
    }

    let local = parts[0];
    let domain = parts[1];

    if local.is_empty() || domain.is_empty() {
        return false;
    }

    if !domain.contains('.') {
        return false;
    }

    true
}

#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn test_is_valid_email() {
        assert!(is_valid_email("user@example.com"));
        assert!(is_valid_email("user@domain.org"));
    }

    #[test]
    fn test_is_valid_email_invalid() {
        assert!(!is_valid_email(""));
        assert!(!is_valid_email("noatsign"));
        assert!(!is_valid_email("@nodomain.com"));
        assert!(!is_valid_email("user@"));
        assert!(!is_valid_email("user@nodot"));
        assert!(!is_valid_email("user@@double.com"));
    }
}

